INSERT INTO `assistant_article` VALUES (4, 'Android 11 Beta版来了：6月1日见', '由于疫情原因，谷歌此前推迟了Android 11 Beta的推出时间，不过今天正式宣布，将于6月1日线上推出。', 'http://www.antutu.com/doc/121854.htm', 'http://img.antutu.com/20200529/thumb_1590734665.5614.jpg', '17小时前', 1922, 10);
INSERT INTO `assistant_article` VALUES (5, '100%真全面屏 Redmi K30 Pro限时降价', '5月29日，Redmi手机官方微博宣布旗下的Redmi K30 Pro标准版将开启限时优惠降价。', 'http://www.antutu.com/doc/121853.htm', 'http://img.antutu.com/20200529/thumb_1590720766.1331.jpg', '21小时前', 4549, 32);
INSERT INTO `assistant_article` VALUES (6, '天玑800加持 中兴新机配置曝光', '中兴的5G新机Axon 11 SE已经官宣了将在6月1日下午发布，距离发布会时间还有3天的时间。', 'http://www.antutu.com/doc/121852.htm', 'http://img.antutu.com/20200529/thumb_1590720084.57.jpg', '21小时前', 1651, 6);
INSERT INTO `assistant_article` VALUES (7, '高通发布Wi-Fi 6E芯片：可集成于骁龙SoC、速度剧增', '近日，美国FCC（联邦通信委员会）决定开放新的非授权6GHz频段，与此同时，Wi-Fi联盟开始建立新的Wi-Fi 6E标准，首次引入6GHz频段。', 'http://www.antutu.com/doc/121850.htm', 'http://img.antutu.com/20200529/thumb_1590719821.9382.png', '22小时前', 1927, 5);
INSERT INTO `assistant_article` VALUES (8, '史上首次 iPhone全系官方降价参加618', '5月29日凌晨0点，苹果官方上线优惠活动，正式宣布加入618年中大促，这也是苹果首次带来的官方大幅度降价活动。', 'http://www.antutu.com/doc/121849.htm', 'http://img.antutu.com/20200529/thumb_1590718158.5384.jpg', '22小时前', 3008, 10);
INSERT INTO `assistant_article` VALUES (9, '小米10/Redmi K30 Pro降价：最低2699元', '一年一次的618活动即将拉开帷幕，各大电商平台开始了预热，小米商城也不例外。', 'http://www.antutu.com/doc/121848.htm', 'http://img.antutu.com/20200529/thumb_1590717175.3497.jpg', '22小时前', 2670, 16);
INSERT INTO `assistant_article` VALUES (10, '苹果原型机曝光 摄像头几经波折', 'iPod touch这款产品可以说是不少苹果用户体验iOS的首款设备，即使它不能打电话。', 'http://www.antutu.com/doc/121847.htm', 'http://img.antutu.com/20200529/thumb_1590716998.4169.jpg', '22小时前', 1717, 1);
INSERT INTO `assistant_article` VALUES (11, '魅族双屏工程机曝光：圆形副屏、八曲面设计', '魅族曾在Pro 7系列尝试过双屏设计，除了正面完整屏幕以外，机身背面还有一块副屏，用以显示自拍、天气等快捷信息。', 'http://www.antutu.com/doc/121846.htm', 'http://img.antutu.com/20200529/thumb_1590716356.7738.jpg', '22小时前', 1468, 9);
INSERT INTO `assistant_article` VALUES (12, 'OPPO Reno 4系列官宣：65W超级闪充', '5月29日上午，OPPO官方宣布，将于6月5日晚19点30分召开线上新品发布会，正式发布旗下的Reno 4系列。', 'http://www.antutu.com/doc/121845.htm', 'http://img.antutu.com/20200529/thumb_1590716277.5954.jpg', '22小时前', 1705, 11);
INSERT INTO `assistant_article` VALUES (13, 'DXO公布最佳榜单 P40 Pro拿下多项第一', 'DXOMARK发布长文章，按照照片、视频、广角、夜间摄影、变焦以及散景六大分类汇集了DXOMARK目前最欣赏的智能手机摄像头机型。', 'http://www.antutu.com/doc/121844.htm', 'http://img.antutu.com/20200529/thumb_1590716126.6333.jpg', '22小时前', 2066, 15);
INSERT INTO `assistant_article` VALUES (14, '全新半导体技术宣布！5nm产能飙升600%', '荷兰ASML公司是目前全球唯一EUV光刻机供应商，7nm制程工艺的芯片都要使用ASML的设备，现在他们又带来了一项全新的设备，适用于5nm及更先进的工艺，提升生产效率。', 'http://www.antutu.com/doc/121843.htm', 'http://img.antutu.com/20200529/thumb_1590715444.9055.jpg', '23小时前', 6443, 18);
INSERT INTO `assistant_article` VALUES (15, '新版微信发布：一大波贴心小功能', '5月28日晚，Android微信7.0.15版正式发布，新增了不少有用的小功能。', 'http://www.antutu.com/doc/121842.htm', 'http://img.antutu.com/20200529/thumb_1590714592.8115.jpg', '23小时前', 1401, 3);
INSERT INTO `assistant_article` VALUES (16, '史上最强限定机 OPPO Ace2特别版发布', '5月28日下午，OPPO举行了线上直播发布会，正式带来了OPPO Ace2 EVA限定版手机。', 'http://www.antutu.com/doc/121841.htm', 'http://img.antutu.com/20200528/thumb_1590652208.7613.jpg', '1天前', 4701, 20);
INSERT INTO `assistant_article` VALUES (17, '三星5nm Exynos SoC即将量产：性能大涨', '上周，三星5nm代工厂动工的消息开始传出，该生产线位于韩国平泽市，具体量产时间并不知晓。', 'http://www.antutu.com/doc/121840.htm', 'http://img.antutu.com/20200528/thumb_1590651560.7967.jpg', '1天前', 2483, 18);
